INSERT INTO `component` VALUES ('65', '2', '分页条目', 'PageBar', '//分页条目组件\n    class PageBar extends React.Component{\n	    render() {\n	        var idPrefix = \"__id_ax_paging_\";\n			var pageNumberClass = \"page_number\";\n			var currentPageNumberClass = \"active\";\n			var pageNumberSeparatorClass = \"page_number_separator\";\n			var prevPageText = \"上一页\";\n			var nextPageText = \"下一页\";\n	        var pageInfo = this.props.pageInfo;\n			var pageBar = $(this.refs.pageBar);\n			/*页码*/\n			var pageIndex = this.props.pageIndex;\n			var pageIndexHolder = pageIndex;\n			/*每页显示最大条数*/\n			var pageSize = this.props.pageSize;\n			var pageSizeHolder = pageSize;\n			/*总条数*/\n			var matchedTotal = this.props.matchedTotal;\n			/*总页数*/\n			var pageTotal = Math.floor((matchedTotal - 1) / pageSize) + 1;\n			var pageTotalHolder = pageTotal;\n			/*中间显示多少页码数*/\n			var centerPageButtonQuantity = 5;\n			/*前后各显示多少页码数*/\n			var edgePageButtonQuantity = 2;\n			var entryQuantity = centerPageButtonQuantity + edgePageButtonQuantity + edgePageButtonQuantity;\n			var pagingBarHtml = \"\";\n			pagingBarHtml += \"<li><a id=\'\" + idPrefix + \"prevPage\' href=\'javascript:;\' >\" + prevPageText + \"</a></li>\";\n			// 12 345 67   12 345..78     12..456..89    12..456..9 10\n			var entryQuantity = centerPageButtonQuantity + edgePageButtonQuantity + edgePageButtonQuantity;\n			if(entryQuantity >= pageTotal){\n				for(var i = 1; i <= pageTotal; i++){\n					pagingBarHtml += \"<li><a id=\'\" + idPrefix + \"page_\" + i + \"\'  href=\'javascript:;\'>\" + i + \"</a></li>\";\n				}\n			}else{\n				var overflow = pageTotal - entryQuantity;\n				var center = Math.round(pageTotal / 2);\n				// 4567    345678    23456789\n				var centerStart, centerEnd;\n				if(centerPageButtonQuantity % 2 == 0){\n					var half = Math.floor(centerPageButtonQuantity / 2);\n					centerStart = center - half + 1;\n					centerEnd = center + half;\n				}else{\n					var half = Math.floor(centerPageButtonQuantity / 2);\n					centerStart = center - half;\n					centerEnd = center + half;\n				}\n				\n				for(var i = 1; i <= edgePageButtonQuantity; i++){\n					pagingBarHtml += \"<li><a id=\'\" + idPrefix + \"page_\" + i + \"\' class=\'\" + pageNumberClass + \"\' href=\'javascript:;\'>\" + i + \"</a></li>\";\n				}\n				\n				if(overflow > 1){\n					pagingBarHtml += \"<li><span class=\'\" + pageNumberSeparatorClass + \"\'>...</span></li>\";\n				}\n				\n				for(var i = centerStart; i <= centerEnd; i++){\n					pagingBarHtml += \"<li><a id=\'\" + idPrefix + \"page_\" + i + \"\' href=\'javascript:;\'>\" + i + \"</a></li>\";\n				}\n				\n				pagingBarHtml += \"<li><span class=\'\" + pageNumberSeparatorClass + \"\'>...</span></li>\";\n				\n				for(var i = pageTotal - edgePageButtonQuantity + 1; i <= pageTotal; i++){\n					pagingBarHtml += \"<li><a id=\'\" + idPrefix + \"page_\" + i + \"\' href=\'javascript:;\'>\" + i + \"</a></li>\";\n				}\n					\n			}\n			pagingBarHtml += \"<li><a id=\'\" + idPrefix + \"nextPage\' href=\'javascript:;\'>\" + nextPageText + \"</a></li>\";\n			pageBar.html(pagingBarHtml);\n			pageBar.find(\"#\" + idPrefix + \"prevPage\").on(\"click\", function(){\n				pageIndexHolder--;\n				$(this).siblings().removeClass(currentPageNumberClass);\n				pageInfo.componentRequest({\n					pageIndex: pageIndexHolder,\n				    pageSize: pageSizeHolder\n			      });\n			});\n			pageBar.find(\"#\" + idPrefix + \"nextPage\").on(\"click\", function(){\n				pageIndexHolder++;\n				$(this).siblings().removeClass(currentPageNumberClass);\n				pageBar.find(\"#\" + idPrefix + \"page_\" + pageIndexHolder).addClass(currentPageNumberClass);//在此附加css，是为了能立即显示出当前页的css样式\n				pageInfo.componentRequest({\n					pageIndex: pageIndexHolder,\n				    pageSize: pageSizeHolder\n			      });\n			});\n			pageBar.find(\"[id^=\" + idPrefix + \"page_\" + \"]\").on(\"click\", function(){\n				pageIndexHolder = parseInt($(this).text());\n				$(this).siblings().removeClass(currentPageNumberClass);\n				$(this).addClass(currentPageNumberClass);//在此附加css，是为了能立即显示出当前页的css样式\n				pageInfo.componentRequest({\n					pageIndex: pageIndexHolder,\n				    pageSize: pageSizeHolder\n			      });\n			});\n			handlePageChange();\n			function handlePageChange(){\n				handlePageButtonAbled();\n				handleCurrentPageButtonClass();\n				handleScale();\n			}\n			\n			//首页与码页1与上一页：在第一页时不可用，尾页与码页最大值与下一页：在最大页时不可用\n			function handlePageButtonAbled(){\n				if(pageIndexHolder == 1){\n					pageBar.find(\"#\" + idPrefix + \"page_1\").unbind(\"click\");\n					pageBar.find(\"#\" + idPrefix + \"prevPage\").unbind(\"click\");\n				}\n				if(pageIndexHolder >= pageTotalHolder){\n					pageBar.find(\"#\" + idPrefix + \"page_\" + pageTotalHolder).unbind(\"click\");\n					pageBar.find(\"#\" + idPrefix + \"nextPage\").unbind(\"click\");\n				}\n			}\n			\n			function handleCurrentPageButtonClass(){\n				pageBar.find(\"#\" + idPrefix + \"page_\" + pageIndexHolder).parent().addClass(currentPageNumberClass);\n			}\n			\n			function handleScale(){\n				pageBar.find(\"#\" + idPrefix + \"pageIndex\").text(pageIndexHolder);\n			}\n			return (\n			    <div>\n			      <ul className=\"pagination\" ref=\"pageBar\"></ul>\n			    </div>\n			  );\n	    }\n	}', '1', null, '2018-01-22 15:00:22', null, '2018-01-24 20:27:14', null);
INSERT INTO `component` VALUES ('66', '2', '表格分页', 'TablePage', '//表格分页组件\n	class TablePage extends React.Component{\n		constructor(){\n			super();\n	        this.state = {\n		      success:true,\n		      pageIndex: 1,\n		      pageSize: 10,\n		      matchedTotal: 0,\n		      resultSet:[]\n		    };\n		}\n		//请求接口函数\n		componentRequest(optionsParam){\n		  var interUrl = this.props.source.url;\n		  var param = this.props.source.param;\n		  var info = this.props.info;\n		  //条件项组件\n		  var myContainer = $(ReactDOM.findDOMNode(info.refs.myContainer));\n		  var myContainerTarget = myContainer.find(\".selected\");\n		  //条件Tab页签组件\n		  var tabPage = $(ReactDOM.findDOMNode(info.refs.tabPage));\n		  var tabPageTarget = tabPage.find(\".current\");\n		  var tabPageParam = tabPageTarget.attr(\"data-option-param\");\n		  var tabPageValue = tabPageTarget.attr(\"data-option-value\");\n		  //排序组件\n		  var sortQuery = $(ReactDOM.findDOMNode(info.refs.tabPage.refs.sortQuery));\n		  var sortQueryTarget = sortQuery.find(\".active_option\");\n		  var sortQueryParam = sortQueryTarget.attr(\"data-option-param\");\n		  var sortQueryValue = sortQueryTarget.attr(\"data-option-value\");\n		  var sortQueryType = sortQueryTarget.attr(\"data-option-type\");\n		  //输入框\n		  var userInput = $(ReactDOM.findDOMNode(info.refs.tabPage.refs.userInput));\n		  var userInputParam = userInput.attr(\"data-option-param\");\n		  var userInputValue = userInput.val();\n		  var defaultOptions1 = param;\n		  if(myContainerTarget.length > 0){\n		  	myContainerTarget.each(function(){\n		  		var myContainerParam = $(this).attr(\"data-option-param\");\n		  		var myContainerValue =  $(this).attr(\"data-option-value\");\n		  		if(myContainerParam){\n			    	var str = myContainerParam.split(\"-\");\n			    	var str1 = myContainerValue.split(\"-\");\n			    	if(str[0] && str1[0]){\n			    		defaultOptions1[str[0]] = str1[0];\n			    	}\n			    	if(str[1] && str1[1]){\n			    		defaultOptions1[str[1]] = str1[1];\n			    	}\n			    }\n		  	});\n		  }\n		  if(tabPageTarget.length > 0){\n		  	defaultOptions1[tabPageParam] = tabPageValue;\n		  }\n		  if(sortQueryTarget.length > 0){\n		  	defaultOptions1[sortQueryParam] = sortQueryValue+\" \"+sortQueryType;\n		  }\n		  if(userInput.length > 0){\n		  	defaultOptions1[userInputParam] = userInputValue;\n		  }\n		  \n		  var optionsParam1 = $.extend(defaultOptions1, optionsParam||{});;\n		  var optionCallback = {\n					\"onSuccess\":function(json, textStatus, xhr){\n						this.setState({\n						    success:true,\n							pageIndex: json.pageIndex,\n						    pageSize: json.pageSize,\n						    matchedTotal: json.matchedTotal,\n					        resultSet: json.resultSet\n					      });\n					}.bind(this),\n					\"onFailure\": function(xhr, textStatus, error){\n						this.setState({\n						    success:false\n					      });\n					}.bind(this)\n			};\n		    this.serverRequest = getNotPageListDataByParam(interUrl ,optionsParam1, optionCallback);\n		  }\n		  //在组件从 DOM 中移除的时候立刻被调用\n		  componentWillUnmount() {\n		    this.serverRequest.abort();\n		  }\n		  //在组件完成更新后立即调用。在初始化时不会被调用\n		  componentDidUpdate() {\n		    if(this.state.success){\n		    	var data_me = this.props.data_me;\n		    	//渲染表头\n			    var dataThead = $(this.refs.dataThead);\n			    dataThead.empty();\n			    var tr = $(\"<tr>\");\n		    	$.each(data_me.head, function(i, headItem){\n			    	var th = $(\"<th>\",headItem);\n			    	tr.append(th);\n			    });\n			    dataThead.append(tr);\n			    //渲染表格数据\n			    var dataTbody = $(this.refs.dataTbody);\n			    dataTbody.empty();\n			    $.each(this.state.resultSet, function(i, rowItem){\n			    	var rowtr = $(\"<tr>\");\n			    	$.each(data_me.body, function(i, bodyItem){\n			    		var td = $(\"<td>\",{\"html\":\"--\"});\n			    		if(bodyItem.isshow){\n			    			var tdHtml = bodyItem.callbackFun(rowItem[bodyItem.key]);\n				    		td = $(\"<td>\",{\"html\":tdHtml});\n			    		}\n				    	rowtr.append(td);\n				    });\n			    	dataTbody.append(rowtr);\n			    });\n		    } else {\n		    	var dataTbody = $(this.refs.dataTbody);\n		    	dataTbody.html(\"<div>加载失败, 请联系管理人员。</div>\");\n		    }\n		  }\n	      render() {\n	        //  当组件插入到 DOM 后，ref 属性添加一个组件的引用于到 this.refs\n	        return (\n	          <div data-json-ref=\"data_table_page\">\n	            <table className=\"table table-striped table-bordered table-hover dataTable no-footer dtr-inline\">\n					<thead ref=\"dataThead\">\n					</thead>\n					<tbody ref=\"dataTbody\" role=\"alert\" aria-live=\"polite\" aria-relevant=\"all\">\n					</tbody>\n				</table>\n	            <PageBar pageIndex={this.state.pageIndex} pageSize={this.state.pageSize} matchedTotal={this.state.matchedTotal} pageInfo={this}/>\n	          </div>\n	        );\n	      }\n	}', '1', null, '2018-01-22 19:00:49', null, '2018-01-27 01:15:12', null);
INSERT INTO `component` VALUES ('67', '2', '条件选项', 'ContainerDiv', '//条件项组件\n	class ContainerDiv extends React.Component{\n		constructor(){\n			super();\n	        this.state = {\n		      query_info:\"\"\n		    };\n		}\n		//绑定条件项点击事件\n		myClick(event){\n		  if(this.state.query_info){\n		    $(event.target).parent(\"li\").siblings().removeClass(\"selected\");\n		    $(event.target).parent(\"li\").addClass(\"selected\");\n		  	this.state.query_info.componentRequest({\n		  		pageIndex: 1,\n			    pageSize: 10\n		  	});\n		  }\n		}\n		render() {\n		var list = this.props.data_me;\n		var lis = list.map(function (item) {\n		  var paramInfo = item.title.param_info;\n		  var optionList = item.options;\n		  var optList = optionList.map(function (optItem) {\n		  	 return  (\n  	 			<li className=\"a_option_container\" data-option-param={paramInfo.param_field} data-option-value={optItem.value}> <a href=\"#\" onClick={this.myClick.bind(this)} >{optItem.name}</a> </li>\n					);\n		  }.bind(this));\n		  return  (\n		  	<div>\n				<div className=\"navbar-header\">\n					<a className=\"navbar-brand a_title_container\" href=\"#\">{item.title.name}</a>\n				</div>\n				<div className=\"collapse navbar-collapse\" id=\"bs-example-navbar-collapse-1\">\n					<ul className=\"nav navbar-nav\">\n						{optList}\n					</ul>\n				</div>\n			</div>\n		  );\n		}.bind(this));\n        return (\n        \n			<div data-json-ref=\"data_container\" ref=\"div_container\">\n	        	<nav className=\"navbar navbar-default\" role=\"navigation\">\n	        		{lis}\n				</nav>\n			</div>\n        );\n      }\n	}', '1', null, '2018-01-22 19:05:21', null, '2018-01-29 12:08:37', null);
INSERT INTO `component` VALUES ('68', '2', '排序选项', 'SortSpan', '//排序项组件\n	class SortSpan extends React.Component{\n		constructor(){\n			super();\n	        this.state = {\n			  query_info:\"\"\n			};\n		}\n		//绑定排序项点击事件\n		myClick(event){\n		  if(this.state.query_info){\n		    $(\".div_option_sort\").removeClass(\"active_option\");\n			$(event.target).addClass(\"active_option\");\n		  	this.state.query_info.componentRequest({\n		  		pageIndex: 1,\n			    pageSize: 10\n		  	});\n		  }\n		}\n		render() {\n		var list = this.props.data_me;\n		var lis = list.map(function (item) {\n		  var optionList = item.param_info;\n		  var optList = optionList.map(function (optItem) {\n		  	 return  (\n		  	 <b className=\"div_option_sort\" data-option-type={optItem.param_type} data-option-param={optItem.param_field} data-option-value={optItem.param_value} onClick={this.myClick.bind(this)}></b>\n					);\n		  }.bind(this));\n		  return  (\n			<li data-json-ref=\"data_sort_info\" className=\"dropdown\">\n				<a href=\"#\" className=\"dropdown-toggle\">\n					{item.title}\n					{optList}\n				</a>\n			</li>\n		  );\n		}.bind(this));\n        return (\n        	<div className=\"nav navbar-nav\">\n				{lis}\n        	</div>\n        );\n      }\n	}', '1', null, '2018-01-22 19:06:07', null, '2018-01-29 12:08:53', null);
INSERT INTO `component` VALUES ('69', '2', '条件文本框', 'ContainerInput', '//条件文本框组件\n		class ContainerInput extends React.Component{\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;//data_searchinput\n				var className = this.props.data_me.className;//search_text\n				var dataOptionParam = this.props.data_me.dataOptionParam;//cpdm\n				var placeholder = this.props.data_me.placeholder;//请输入相关产品代码\n				return (\n					<input data-json-ref={dataJsonRef} type=\"text\" ref=\"container_input\" className={className} \n					data-option-param={dataOptionParam} placeholder={placeholder} />\n				);\n			}\n		}', '1', null, '2018-01-22 19:06:32', null, '2018-02-07 15:23:34', null);
INSERT INTO `component` VALUES ('70', '2', '查询按钮', 'BtnSpan', '//按钮组件\n	class BtnSpan extends React.Component{\n		constructor(){\n			super();\n	        this.state = {\n			  query_info:\"\"\n			};\n		}\n		//绑定排序项点击事件\n		myClick(event){\n		  if(this.state.query_info){\n		  	this.state.query_info.componentRequest({\n		  		pageIndex: 1,\n			    pageSize: 10\n		  	});\n		  }\n		}\n		render() {\n	        return (\n	        	<button data-json-ref=\"data_btn\" type=\"button\" className={this.props.data_me.className} onClick={this.myClick.bind(this)}>{this.props.data_me.text}</button>\n	        );\n      }\n	}', '1', null, '2018-01-22 19:07:13', null, '2018-01-29 12:09:32', null);
INSERT INTO `component` VALUES ('71', '2', '条件选项卡', 'ContainerTabUl', '//条件选项卡组件\n	class ContainerTabUl extends React.Component{\n		constructor(){\n			super();\n	        this.state = {\n		    	query_info:\"\"\n		  	};\n		}\n		//绑定条件tab页签切换事件\n		myClick(event){\n		  if(this.state.query_info){\n		  	$(event.target).parent(\"li\").siblings().find(\"a\").removeClass(\"current\");\n			$(event.target).addClass(\"current\");\n		  	this.state.query_info.componentRequest({\n		  		pageIndex: 1,\n			    pageSize: 10\n		  	});\n		  }\n		}\n		componentDidUpdate() {\n    		//将条件tab页签切 渲染对象通过状态来传递\n    		this.refs.sortQuery.setState({\n    			query_info:this.state.query_info\n    		});\n    		this.refs.queryBtn.setState({\n    			query_info:this.state.query_info\n    		});\n	    }\n		render() {\n			var list = this.props.data_me;\n			var lis = list.map(function (item) {\n			  return  (\n			    <li>\n					<a href=\"javascript:void(0);\" className={item.current ? \'current\' : \'\'} data-option-param={item.param_field} data-option-value={item.param_value} onClick={this.myClick.bind(this)}>{item.title}</a>\n				</li>\n			  );\n			}.bind(this));\n	        return (\n	        	<nav className=\"navbar navbar-default\" role=\"navigation\" data-json-ref=\"data_container_tabinfo\">\n					<div className=\"collapse navbar-collapse\" id=\"bs-example-navbar-collapse-1\">\n						<ul className=\"nav navbar-nav\">\n							{lis}\n							<SortSpan ref=\"sortQuery\" data_me={this.props.data_sort}/>\n						</ul>\n						<form role=\"search\" className=\"navbar-form navbar-right\">\n\n							<div className=\"form-group\">\n								<ContainerInput ref=\"userInput\" data_me={this.props.data_input}/>\n							</div>\n							<BtnSpan ref=\"queryBtn\" data_me={this.props.data_btn}/>\n						</form>\n					</div>\n				</nav>\n	        );\n      }\n	}', '1', null, '2018-01-22 19:07:53', null, '2018-01-29 12:09:50', null);
INSERT INTO `component` VALUES ('72', '2', '复合主组件--tab页签', 'TabDiv', '//复合主组件 tab页签转化组件11\n	class TabDiv extends React.Component{\n		constructor(){\n			super();\n	        this.state = {\n		    	current_source:\"\"\n		  	};\n		}\n		myClick(obj){\n		  this.setState({\n			current_source:obj.source,\n	      });\n		}\n		componentDidMount() {\n			//第一次渲染 默认是第一个页签\n			this.myClick(this.props.data_me[0]);\n    	}\n    	componentDidUpdate() {\n    		//渲染分页组件\n    		this.refs.myPageInfo.componentRequest({\n    			pageIndex: 1,\n			    pageSize: 10\n    		});\n    		//将条件组件 渲染对象通过状态来传递\n    		this.refs.myContainer.setState({\n    			query_info:this.refs.myPageInfo\n    		});\n    		//将条件tab页签切 渲染对象通过状态来传递\n    		this.refs.tabPage.setState({\n    			query_info:this.refs.myPageInfo\n    		});\n	    }\n		render() {\n		var list = this.props.data_me;\n		var lis = list.map(function (item) {\n		  return  (\n		    <li className={item.active ? \'active\': \'\'}>\n		      <a href=\"#\" data-toggle=\"tab\" onClick={this.myClick.bind(this,item)}>{item.text}</a>\n		    </li>\n		  );\n		}.bind(this));\n        return (\n          <div data-json-ref=\"data_me\">\n            <ul ref=\"myTab\" className=\"nav nav-tabs\">\n				{lis}\n			</ul>\n			<div ref=\"myTabContent\" className=\"tab-content\">\n				<ContainerDiv ref=\"myContainer\" data_me={this.props.data_container}/>\n				\n				<ContainerTabUl ref=\"tabPage\" data_me={this.props.data_container_tabinfo} data_sort={this.props.data_sort_info} data_btn={this.props.data_btn} data_input={this.props.data_input}/>\n				\n				<TablePage ref=\"myPageInfo\" data_me={this.props.data_table_page} source={this.state.current_source} info={this}/>\n			</div>\n          </div>\n        );\n      }\n	}', '1', null, '2018-01-22 19:08:37', null, '2018-01-29 14:04:58', null);
INSERT INTO `component` VALUES ('73', '2', 'Span文本组件', 'SpanText', '//Span文本组件\n		class SpanText extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var className = this.props.data_me.className;\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var text = this.props.data_me.text;\n				return (\n					<span data-json-ref={dataJsonRef} className={className}>\n						{text}\n					</span>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:24:49', null, '2018-02-07 16:24:49', null);
INSERT INTO `component` VALUES ('74', '2', 'Div文本组件', 'DivText', '//Div文本组件\n		class DivText extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var className = this.props.data_me.className;\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var text = this.props.data_me.text;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						{text}\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:27:41', null, '2018-02-07 16:27:41', null);
INSERT INTO `component` VALUES ('75', '2', '背景图片组件', 'DivImg', '//背景图片组件\n		class DivImg extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var className = this.props.data_me.className;\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var imgSrc = this.props.data_me.imgSrc;\n				var img = \"\";\n				if(imgSrc==\'no\'){\n				} else {\n					img = (<img src={imgSrc}/>);\n				}\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						{img}\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:28:07', null, '2018-02-07 16:28:07', null);
INSERT INTO `component` VALUES ('76', '2', '用户信息组件', 'UserInfo', '//用户信息组件\n		class UserInfo extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var userinfo = this.props.data_me;\n				var dataJsonRef = userinfo.dataJsonRef;\n				//欢迎语文本\n				var wecomeText = userinfo.wecomeText;\n				//欢迎语样式\n				var wecomeClass = userinfo.wecomeClass;\n				//用户信息样式\n				var infoClass = userinfo.infoClass;\n				//登出文本\n				var loginoutText = userinfo.loginoutText;\n				//登出样式\n				var loginoutClass = userinfo.loginoutClass;\n				//登出点击事件函数\n				var loginoutClick = userinfo.loginoutClick;\n				//用户信息文本\n				var infoText = \"\";\n				if(isLogin){\n					var isMain=isMainUser?\"【主用户】\":\"【操作用户】\";\n					if(typeof HYDM==\"undefined\" || HYDM==\"\" || HYDM==\"null\"){\n						infoText = (isMain+\" \"+userName);\n					}else{\n						infoText = (isMain+\" \"+HYDM+\" \"+userName);\n					}\n				}else{\n					infoClass += \" hidden\";\n					loginoutClass += \" hidden\";\n				}\n				return (\n					<div data-json-ref={dataJsonRef} className=\"has_login\">\n					  <span className={infoClass}>{infoText}</span>\n					  <span>&nbsp;{wecomeText}</span>\n					  <span className={loginoutClass} onclick={loginoutClick}>{loginoutText}</span>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:28:30', null, '2018-02-07 16:28:30', null);
INSERT INTO `component` VALUES ('77', '2', '面包屑导航组件', 'BreadcrumbMe', '//面包屑导航组件\n		class BreadcrumbMe extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var breadcrumbme = this.props.data_me;\n				var dataJsonRef = breadcrumbme.dataJsonRef;\n				var className = breadcrumbme.className;\n				var myClick = breadcrumbme.myClick;\n				var lis = breadcrumbme.dataList.map(function (item) {\n					if(item.type==\'link\'){\n						return  (\n						<div className={item.className} data-url={item.url} data-plate={item.plate} onClick={myClick}>\n						  {item.text}\n						</div>\n					  );\n					} else if(item.type==\'icon\'){\n						var data_me = {};\n						data_me[\"dataJsonRef\"] = item.dataJsonRef;\n						data_me[\"className\"] = item.className;\n						data_me[\"imgSrc\"] = item.imgSrc;\n						return  (\n						<DivImg  data_me={data_me}/>\n					  );\n					}\n				  \n				}.bind(this));\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						{lis}\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:28:50', null, '2018-02-07 16:28:50', null);
INSERT INTO `component` VALUES ('78', '2', '登陆条组件', 'LoginBar', '//登陆条组件\n		class LoginBar extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				return (\n					\n					<div data-json-ref={dataJsonRef} className={className}>\n						<div className=\"login_bar_content\">\n							<div className=\"left_wel_tips\">\n								<UserInfo ref=\"div_userinfo\" data_me={this.props.data_userinfo}/>\n							</div>\n							<div className=\"right_wel_tips\">\n								<BreadcrumbMe ref=\"div_breadcrumbme\" data_me={this.props.data_breadcrumbme}/>\n							</div>\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:29:11', null, '2018-02-07 16:29:11', null);
INSERT INTO `component` VALUES ('79', '2', '查询按钮组件', 'SearchBtn', '//查询按钮组件\n		class SearchBtn extends React.Component{\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;//data_searchbtn\n				var className = this.props.data_me.className;//search_btn\n				var text = this.props.data_me.text;//搜索\n				var myClick = this.props.data_me.myClick;//点击事件\n				return (\n					<button data-json-ref={dataJsonRef} type=\"button\" className={className} onClick={myClick}>{text}</button>\n				);\n			}\n		}', '1', null, '2018-02-07 16:29:34', null, '2018-02-07 16:29:34', null);
INSERT INTO `component` VALUES ('80', '2', '右边查询区组件', 'RightSearch', '//右边查询区组件\n		class RightSearch extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<div className=\"search_input\">\n							<DivImg ref=\"input_divimg\" data_me={this.props.data_input_divimg}/>\n							<ContainerInput ref=\"search_input\" data_me={this.props.data_input}/>\n							<SearchBtn ref=\"search_btn\" data_me={this.props.data_btn}/>\n						</div>\n						<BreadcrumbMe ref=\"div_search_breadcrumbme\" data_me={this.props.data_search_breadcrumbme}/>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:29:56', null, '2018-02-07 16:29:56', null);
INSERT INTO `component` VALUES ('81', '2', 'logo区组件', 'LogoArea', '//logo区组件\n		class LogoArea extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<div className=\"logo_area\">\n							<div className=\"logo_left\">\n								<DivImg ref=\"div_logoimg\" data_me={this.props.data_logoimg}/>\n							</div>\n							<RightSearch ref=\"div_rightsearch\" data_me={this.props.data_rightsearch}\n							data_input_divimg={this.props.data_input_divimg} \n							data_input={this.props.data_input} \n							data_btn={this.props.data_btn} \n							data_search_breadcrumbme={this.props.data_search_breadcrumbme}/>\n\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:30:16', null, '2018-02-07 16:30:16', null);
INSERT INTO `component` VALUES ('82', '2', '导航二级菜单条目组件', 'NavSecondMenuItem', '//导航二级菜单条目组件\n		class NavSecondMenuItem extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				let dataMe = this.props.data_me;\n				let dataJsonRef = dataMe.dataJsonRef;\n				let className = dataMe.className;\n				let dataSource = dataMe.dataSource;\n				var myClick = this.props.data_click;\n				var liInfo = dataSource.map(function (item) {\n					let liClassName = item.className;\n					let url = item.url;\n					let isLogin = item.isLogin;\n					let plate = item.plate;\n					let text = item.text;\n					let type = item.type;\n					return  (\n								<li className={liClassName}>\n									<div data-second-link data-url={url} data-plate={plate} data-type={type} data-is-login={isLogin} onClick={myClick}>{text}</div>\n								</li>\n							);\n				}.bind(this));\n				\n				return (\n					<ul data-json-ref={dataJsonRef} className={className}>\n						{liInfo}\n					</ul>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:30:37', null, '2018-02-07 16:30:37', null);
INSERT INTO `component` VALUES ('83', '2', '导航二级菜单组件', 'NavSecondMenu', '//导航二级菜单组件\n		class NavSecondMenu extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				let dataMe = this.props.data_me;\n				let dataJsonRef = dataMe.dataJsonRef;\n				//样式\n				let className = dataMe.className;\n				//内容样式\n				let contentClassName = dataMe.contentClassName;\n				//分割线\n				let segLineDivImg = dataMe.segLineDivImg;\n				let onMouseEnter = dataMe.onMouseEnter;\n				let onMouseLeave = dataMe.onMouseLeave;\n				let myClick = this.props.data_click;\n				let dataSource = dataMe.dataSource;\n				var i=0;\n				var divs = dataSource.map(function (item) {\n					i++;\n					if(i<=3){\n						var secClassName = item.className;\n						var titleClassName = item.titleClassName;\n						var spanText = item.spanText;\n						var divImg = item.divImg;\n						var linkGroup = item.linkGroup;\n						if(i==3){\n							return  (\n								<div className={secClassName}>\n									<div className={titleClassName}>\n										<SpanText data_me={spanText}/>\n										<DivImg data_me={divImg}/>\n									</div>\n									<NavSecondMenuItem data_me={linkGroup} data_click={myClick} />\n								</div>\n							);\n						} else {\n							return  (\n								<span><div className={secClassName}>\n									<div className={titleClassName}>\n										<SpanText data_me={spanText}/>\n										<DivImg data_me={divImg}/>\n									</div>\n									<NavSecondMenuItem data_me={linkGroup} data_click={myClick} />\n								</div>\n								<DivImg data_me={segLineDivImg}/></span>);\n						}\n					}	\n				}.bind(this));\n				return (\n					<div data-content className={className}>\n						<div className={contentClassName}>\n							{divs}\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:31:00', null, '2018-02-07 16:31:00', null);
INSERT INTO `component` VALUES ('84', '2', '导航菜单条目组件', 'NavMenuItem', '//导航菜单条目组件\n		class NavMenuItem extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				let dataMe = this.props.data_me;\n				let dataJsonRef = dataMe.dataJsonRef;\n				let className = dataMe.className;\n				let text = dataMe.text;\n				let url = dataMe.url;\n				let plate = dataMe.plate;\n				let isSecond = dataMe.isSecond;\n				let isCurrentPage = dataMe.isCurrentPage;\n				let myClick = this.props.data_click;\n				let liInfo = (<div></div>);\n				if(isSecond){\n					var arrowTopDivImg = dataMe.arrowTopDivImg;\n					var secondMenu = dataMe.secondMenu;\n					if(isCurrentPage){\n						liInfo = (<li data-json-ref={dataJsonRef} className={className}><span data-current-page> {text} <DivImg data_me={arrowTopDivImg}/></span><NavSecondMenu data_me={secondMenu} data_click={myClick}/></li>);\n					} else {\n						liInfo = (<li data-json-ref={dataJsonRef} className={className}><span data-url={url} data-plate={plate} onClick={myClick}> {text} <DivImg data_me={arrowTopDivImg}/></span><NavSecondMenu data_me={secondMenu} data_click={myClick}/></li>);\n					}\n				} else {\n					if(isCurrentPage){\n						liInfo = (<li data-json-ref={dataJsonRef} className={className}><span data-current-page> {text} </span></li>);\n					} else {\n						liInfo = (<li data-json-ref={dataJsonRef} className={className}><span data-url={url} data-plate={plate} onClick={myClick}> {text} </span></li>);\n					}\n				}\n				\n				return liInfo;\n		  }\n		}', '1', null, '2018-02-07 16:31:20', null, '2018-02-07 16:31:20', null);
INSERT INTO `component` VALUES ('85', '2', '导航菜单组件', 'NavMenu', '//导航菜单组件\n		class NavMenu extends React.Component{\n			constructor(){\n				super();\n				this.state = {\n				  success:false\n				};\n			}\n			componentDidMount(){\n				this.setState({\n				  success:true\n				});\n			}\n			componentDidUpdate() {\n				if(this.state.success){\n					let dataMe = this.props.data_me;\n					let onMouseEnter = dataMe.onMouseEnter;\n					let onMouseLeave = dataMe.onMouseLeave;\n					let className = dataMe.className;\n					let menuUl = $(this.refs.menuUl);\n					$(menuUl).find(\"li\").unbind(\"hover\").hover(onMouseEnter, onMouseLeave);\n					let onMouseEnterForTwo = dataMe.onMouseEnterForTwo;\n					let onMouseLeaveForTwo = dataMe.onMouseLeaveForTwo;\n					$(menuUl).find(\"[data-second-link]\").unbind(\"hover\").hover(onMouseEnterForTwo, onMouseLeaveForTwo);\n					\n				} else {\n					\n				}\n		    }\n			render() {\n				let dataMe = this.props.data_me;\n				let dataJsonRef = dataMe.dataJsonRef;\n				let className = dataMe.className;\n				let contentClassName = dataMe.contentClassName;\n				let ulClassName = dataMe.ulClassName;\n				let dataSource = dataMe.dataSource;\n				let myClick = dataMe.myClick;\n				let menuList = dataSource.map(function (item) {\n					return  (\n					<NavMenuItem  data_me={item} data_click={myClick}/>\n				  );\n				  \n				}.bind(this));\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<div className={contentClassName}>\n							<ul className={ulClassName} ref=\"menuUl\">\n								{menuList}\n							</ul>\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:31:45', null, '2018-02-07 16:31:45', null);
INSERT INTO `component` VALUES ('86', '2', '网站头部组件', 'TopArea', '//网站头部组件\n		class TopArea extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {	\n				let dataMe = this.props.data_me;\n				let dataJsonRef = dataMe.dataJsonRef;\n				let className = dataMe.className;\n				let data_login_bar = this.props.data_login_bar;\n				let data_userinfo = this.props.data_userinfo;\n				let data_breadcrumbme = this.props.data_breadcrumbme;\n				let data_logo_area = this.props.data_logo_area;\n				let data_logoimg = this.props.data_logoimg;\n				let data_rightsearch = this.props.data_rightsearch;\n				let data_input_divimg = this.props.data_input_divimg;\n				let data_input = this.props.data_input;\n				let data_btn = this.props.data_btn;\n				let data_search_breadcrumbme = this.props.data_search_breadcrumbme;\n				let data_nav_menu = this.props.data_nav_menu;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<LoginBar data_me={data_login_bar} data_userinfo={data_userinfo}  data_breadcrumbme={data_breadcrumbme}/>\n						<LogoArea data_me={data_logo_area} data_logoimg={data_logoimg} data_rightsearch={data_rightsearch} data_input_divimg={data_input_divimg} data_input={data_input} data_btn={data_btn} data_search_breadcrumbme={data_search_breadcrumbme}/>\n						<NavMenu data_me={data_nav_menu} />\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:32:05', null, '2018-02-07 16:32:05', null);
INSERT INTO `component` VALUES ('87', '2', '网站底部组件', 'BottomArea', '//网站底部组件\n		class BottomArea extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;//data-bottom-area\n				var className = this.props.data_me.className;//bottom\n				var data_wblj_title = this.props.data_wblj_title;\n				var titleDataJsonRef = data_wblj_title.dataJsonRef;\n				var titleClassName = data_wblj_title.className\n				var dataSource = data_wblj_title.dataSource;\n				var titleList = dataSource.map(function (item) {\n					return  (\n					<SpanText  data_me={item}/>\n				  );\n				  \n				}.bind(this));\n				var data_zj_glwyh = this.props.data_zj_glwyh;//版权所有@2015中证机构间报价系统股份有限公司\n				var data_zj_xh = this.props.data_zj_xh;//京ICP备13025901-1号 京公安网安备110102000062-1\n				var data_gzh_img = this.props.data_gzh_img;\n				var data_gzh_tips = this.props.data_gzh_tips;//报价系统微信公众号\n				var data_app_img = this.props.data_app_img;\n				var data_app_tips = this.props.data_app_tips;//手机APP/Android\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<div className=\"bottom_content\">\n							<div className=\"wblj\">\n								<div className={titleClassName} data-json-ref={titleDataJsonRef}>\n									{titleList}\n								</div>\n								<DivText  data_me={data_zj_glwyh}/>\n								<DivText  data_me={data_zj_xh}/>\n							</div>\n							<div className=\"wx_gzh\">\n								<DivImg  data_me={data_gzh_img}/>\n								<DivText  data_me={data_gzh_tips}/>\n							</div>\n							<div className=\"sj_ewm\">\n								<DivImg  data_me={data_app_img}/>\n								<DivText  data_me={data_app_tips}/>\n							</div>\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-07 16:33:02', null, '2018-02-07 16:33:02', null);
INSERT INTO `component` VALUES ('88', '2', '轮播图组件', 'BannerDiv', 'class BannerDiv extends React.Component{\n			constructor(){\n				super();\n				this.state = {\n				  success:false,\n				  result:false,\n				  list:null\n				};\n			}\n			componentDidMount(){\n				var url = this.props.data_me.url;//interfacePath+\"/logomagager/logoList.json\"\n				var param = this.props.data_me.param;//{\"thepage\":\'shouye\',\"ascription\":\'05\',\"sort\":\'to_number(logo_sort) desc\'}\n				var optionCallback = {\n					\"onSuccess\":function(json, textStatus, xhr){\n						this.setState({\n						  success:true,\n						  result:json.result,\n						  list:json.list\n						});\n					}.bind(this),\n					\"onFailure\": function(xhr, textStatus, error){\n						this.setState({\n						    success:false\n					      });\n					}.bind(this)\n				};\n				this.serverRequest = $.ajax({\n					type: \"get\",\n					url: url,\n					data: param,\n					dataType: \"json\",\n					success: function(json, textStatus, xhr){\n						optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n					},\n					error: function(xhr, textStatus, error){\n						options2.onFailure.call(undefined, xhr, textStatus, error);\n					}\n				});\n			}\n			componentWillUnmount() {\n				this.serverRequest.abort();\n			}\n			componentDidUpdate() {\n				if(this.state.success && this.state.result){\n					let $_lunbo = $(this.refs.carousel);\n					let imgUl=$(\"<ul>\",{\"class\":\"carousel-inner\"});\n					console.log($_lunbo);\n					$.each(this.state.list,function(i,info){\n						$.each(info.mediaEntityRelaList,function(j,mediaInfo){\n							var logoUrl = info.logoUrl;\n							var imgSrc = imgSrcById(mediaInfo.mediaId);\n								var img = $(\"<img>\",{\"src\":imgSrc,\"style\":\"width:100%;height:100%;\"});\n								var imgLi=$(\"<li>\",{\"class\":\"carousel-item\"});\n								if(logoUrl){\n									var linkA = $(\"<a>\",{\"href\":logoUrl, \"target\":\"_blank\",\"style\":\"cursor: pointer;\"});\n									linkA.append(img);\n									imgLi.append(linkA);\n								} else {\n									imgLi.append(img);\n								}\n								imgUl.append(imgLi);\n						});\n					});\n					$_lunbo.append(imgUl);\n					$_lunbo.FtCarousel({\n						time: 5000\n					});\n				} else {\n					\n				}\n		    }\n			render() {\n				var className = this.props.data_me.className;//banner_area\n				var exClassName = this.props.data_me.exClassName;//example\n				var ftClassName = this.props.data_me.ftClassName;//ft-carousel\n				var dataJsonRef = this.props.data_me.dataJsonRef;//data_banner_div\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<div className={exClassName}>\n							<div className={ftClassName} ref=\"carousel\"></div>\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-02-28 13:19:47', null, '2018-02-28 13:23:40', null);
INSERT INTO `component` VALUES ('89', '2', '超链接按钮组件', 'HrefBtn', '//超链接按钮组件\n		class HrefBtn extends React.Component{\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;//data_href_btn\n				var className = this.props.data_me.className;//reg_btn\n				var text = this.props.data_me.text;//立即注册\n				var myClick = this.props.data_me.myClick;//点击事件\n				return (\n					<a data-json-ref={dataJsonRef} href=\"javascript:void(0)\" className={className} onclick={myClick}>{text}</a>\n				);\n			}\n		}', '1', null, '2018-03-07 19:18:49', null, '2018-03-07 19:18:49', null);
INSERT INTO `component` VALUES ('90', '2', 'DivSpan文本组件', 'DivSpanText', '//DivSpan文本组件\n		class DivSpanText extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var className = this.props.data_me.className;\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var spanInfo = this.props.data_me.spanInfo;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<SpanText data_me={spanInfo}/>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-03-07 19:19:38', null, '2018-03-07 19:19:38', null);
INSERT INTO `component` VALUES ('91', '2', '请求数据标题文本组件', 'RequestDataTitleDivText', 'class RequestDataTitleDivText extends React.Component{\n			constructor(){\n				super();\n				this.state = {\n				  success:false,\n				  resultData:null\n				};\n			}\n			componentDidMount(){\n				var isRequest = this.props.data_me.isRequest;\n				if(isRequest){\n					var methodType = this.props.data_me.methodType;\n					var url = this.props.data_me.url;\n					var param = this.props.data_me.param;\n					var resultData = this.props.data_me.resultData;\n					var isPoll = this.props.data_me.isPoll;\n					var pollMsec = this.props.data_me.pollMsec;\n					var optionCallback = {\n						\"onSuccess\":function(json, textStatus, xhr){\n							if(resultData){\n								this.setState({\n								  success:true,\n								  resultData:json[resultData]\n								});\n							} else {\n								this.setState({\n								  success:true,\n								  resultData:json\n								});\n							}\n							\n						}.bind(this),\n						\"onFailure\": function(xhr, textStatus, error){\n							this.setState({\n								success:false\n							  });\n						}.bind(this)\n					};\n					this.serverRequest = $.ajax({\n							type: methodType,\n							url: url,\n							data: param,\n							dataType: \"json\",\n							success: function(json, textStatus, xhr){\n								optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n							},\n							error: function(xhr, textStatus, error){\n								optionCallback.onFailure.call(undefined, xhr, textStatus, error);\n							}\n						});\n					if(isPoll){\n						setInterval(function () {\n						   this.serverRequest = $.ajax({\n								type: methodType,\n								url: url,\n								data: param,\n								dataType: \"json\",\n								success: function(json, textStatus, xhr){\n									optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n								},\n								error: function(xhr, textStatus, error){\n									optionCallback.onFailure.call(undefined, xhr, textStatus, error);\n								}\n							});\n						}.bind(this), pollMsec);\n					}\n				} else {\n					this.setState({\n					  success:true\n					});\n				}\n			}\n			componentWillUnmount() {\n				this.serverRequest.abort();\n			}\n			componentDidUpdate() {\n				if(this.state.success){\n					var isRequest = this.props.data_me.isRequest;\n					var valKey = this.props.data_me.valKey;\n					var refValKey = \"ref_val_\"+this.props.data_me.valKey;\n					if(isRequest){\n						var callbackFun = this.props.data_me.callbackFun;\n						var resultData = this.state.resultData;\n						var resultInfo = null;\n						if($.isArray(resultData)){\n							resultInfo = resultData[0];\n						} else {\n							resultInfo = resultData;\n						}\n						$(this.refs[refValKey]).html(callbackFun(resultInfo[valKey]));\n					} else {\n						$(this.refs[refValKey]).html(valKey);\n					}\n					\n				} else {\n					\n				}\n		    }\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				var titleClassName = this.props.data_me.titleClassName;\n				var titleText = this.props.data_me.titleText;\n				var unitClassName = this.props.data_me.unitClassName;\n				var unitText = this.props.data_me.unitText;\n				var valClassName = this.props.data_me.valClassName;\n				var valKey = \"ref_val_\"+this.props.data_me.valKey;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<span className={titleClassName}>{titleText}</span>\n						<span className={valClassName} ref={valKey}></span>\n						<span className={unitClassName}>{unitText}</span>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-03-07 19:20:07', null, '2018-03-07 19:20:07', null);
INSERT INTO `component` VALUES ('92', '2', '请求数据文本组件', 'RequestDataDivText', '//请求数据文本组件\n		class RequestDataDivText extends React.Component{\n			constructor(){\n				super();\n				this.state = {\n				  success:false,\n				  resultData:null\n				};\n			}\n			componentDidMount(){\n				var methodType = this.props.data_me.methodType;\n				var url = this.props.data_me.url;\n				var param = this.props.data_me.param;\n				var resultData = this.props.data_me.resultData;\n				var isPoll = this.props.data_me.isPoll;\n				var pollMsec = this.props.data_me.pollMsec;\n				var optionCallback = {\n					\"onSuccess\":function(json, textStatus, xhr){\n						this.setState({\n						  success:true,\n						  resultData:json[resultData]\n						});\n					}.bind(this),\n					\"onFailure\": function(xhr, textStatus, error){\n						this.setState({\n						    success:false\n					      });\n					}.bind(this)\n				};\n				this.serverRequest = $.ajax({\n					type: methodType,\n					url: url,\n					data: param,\n					dataType: \"json\",\n					success: function(json, textStatus, xhr){\n						optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n					},\n					error: function(xhr, textStatus, error){\n						optionCallback.onFailure.call(undefined, xhr, textStatus, error);\n					}\n				});\n				if(isPoll) {\n					setInterval(function () {\n					   this.serverRequest = $.ajax({\n							type: methodType,\n							url: url,\n							data: param,\n							dataType: \"json\",\n							success: function(json, textStatus, xhr){\n								optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n							},\n							error: function(xhr, textStatus, error){\n								optionCallback.onFailure.call(undefined, xhr, textStatus, error);\n							}\n						});\n					}.bind(this), pollMsec);\n				}\n			\n			}\n			componentWillUnmount() {\n				this.serverRequest.abort();\n			}\n			componentDidUpdate() {\n				if(this.state.success){\n					var resultData = this.state.resultData;\n					var resultInfo = null;\n					if($.isArray(resultData)){\n						resultInfo = resultData[0];\n					} else {\n						resultInfo = resultData;\n					}\n					var dataList = this.props.data_me.dataList;\n					dataList.map(function (item) {\n						var valKey = item.valKey;\n						var refValKey = \"ref_val_\"+item.valKey;\n						var callbackFun = item.callbackFun;\n						$(this.refs[refValKey]).html(callbackFun(resultInfo[valKey]));\n					}.bind(this));\n				} else {\n					\n				}\n		    }\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				var dataList = this.props.data_me.dataList;\n				var lis = dataList.map(function (item) {\n					var itemClassName = item.className;\n					var valClassName = item.valClassName;\n					var valKey = \"ref_val_\"+item.valKey;\n					var unitClassName = item.unitClassName;\n					var unitText = item.unitText;\n					return  (\n						<div className={itemClassName}>\n							<span className={valClassName} ref={valKey}></span>\n							<span className={unitClassName}>{unitText}</span>\n						</div>\n					);\n				}.bind(this));\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						{lis}\n					</div>\n				);\n		  }\n		}', '1', null, '2018-03-07 19:21:12', null, '2018-03-07 19:21:12', null);
INSERT INTO `component` VALUES ('93', '2', '统计数据组件', 'StatisticalDiv', '//统计数据组件\n		class StatisticalDiv extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				var investorInfo = this.props.data_me.investorInfo;\n				var bottomClassName = this.props.data_me.bottomClassName;\n				var drjyTitleInfo = this.props.data_me.drjyTitleInfo;\n				var drjyDataInfo = this.props.data_me.drjyDataInfo;\n				var lsjyTitleInfo = this.props.data_me.lsjyTitleInfo;\n				var lsjyDataInfo = this.props.data_me.lsjyDataInfo;\n				var jycpsTitleInfo = this.props.data_me.jycpsTitleInfo;\n				var jycpsDataInfo = this.props.data_me.jycpsDataInfo;\n				var loginBtnInfo = this.props.data_me.loginBtnInfo;\n				var registerClassName = this.props.data_me.registerClassName;\n				var registerSpanInfo = this.props.data_me.registerSpanInfo;\n				var registerBtnInfo = this.props.data_me.registerBtnInfo;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<RequestDataTitleDivText data_me={investorInfo}/>\n						<div className={bottomClassName}>\n							<DivSpanText data_me={drjyTitleInfo}/>\n							<RequestDataDivText data_me={drjyDataInfo}/>\n													\n							<DivSpanText data_me={lsjyTitleInfo}/>\n							<RequestDataDivText data_me={lsjyDataInfo}/>\n							\n							<DivSpanText data_me={jycpsTitleInfo}/>\n							<RequestDataDivText data_me={jycpsDataInfo}/>\n							\n							<SearchBtn data_me={loginBtnInfo}/>\n\n							<div className={registerClassName}>\n								<SpanText data_me={registerSpanInfo}/>\n								<HrefBtn data_me={registerBtnInfo}/>								\n							</div>\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-03-08 09:19:04', null, '2018-03-08 09:19:04', null);
INSERT INTO `component` VALUES ('95', '2', '助手组件', 'AssistantDiv', '//助手组件\n		class AssistantDiv extends React.Component{\n			constructor(){\n				super();\n				this.state = {\n				  success:false\n				};\n			}\n			componentDidMount(){\n				this.setState({\n				  success:true\n				});\n			}\n			componentDidUpdate() {\n				if(this.state.success){\n					window.onscroll=function(){\n						var t =document.documentElement.scrollTop+document.body.scrollTop;\n						var left =$(this.refs.kjdh)[0];\n						  if( t >=200) {\n							 left.style.position=\"fixed\";\n							 left.style.top=\"50%\";\n						\n						}else{\n							  left.style.position=\"absolute\"; \n							  left.style.top=\"1052px\";\n						}\n					}.bind(this);\n					var topMouseEnter = this.props.data_me.topMouseEnter;\n					var topMouseLeave = this.props.data_me.topMouseLeave;\n					var linkMouseEnter = this.props.data_me.linkMouseEnter;\n					var linkMouseLeave = this.props.data_me.linkMouseLeave;\n					$(this.refs.navig_top).unbind(\"hover\").hover(topMouseEnter, topMouseLeave);\n					$(this.refs.navig_list).unbind(\"hover\").hover(linkMouseEnter, linkMouseLeave);\n				}\n		    }\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				var topClassName = this.props.data_me.topClassName;\n				var topImgSrc = this.props.data_me.topImgSrc;\n				\n				var linkClassName = this.props.data_me.linkClassName;\n				var linkImgSrc = this.props.data_me.linkImgSrc;\n				\n				var linkUrl = this.props.data_me.linkUrl;\n				var linkPlate = this.props.data_me.linkPlate;\n				var linkClick = this.props.data_me.linkClick;\n				return (\n					<div data-json-ref={dataJsonRef} className={className} ref=\"kjdh\">\n						<a className={topClassName} ref=\"navig_top\" href=\"#top\">\n							<img src={topImgSrc}/>\n						</a>\n						<div className={linkClassName} ref=\"navig_list\" data-url={linkUrl} data-plate={linkPlate} onClick={linkClick}>\n							<img src={linkImgSrc}/>\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-03-08 09:25:24', null, '2018-03-08 09:25:24', null);
INSERT INTO `component` VALUES ('96', '2', '板块展示组件', 'ItemShowDiv', 'class ItemShowDiv extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				var consClassName = this.props.data_me.consClassName;\n				var barClassName = this.props.data_me.barClassName;				\n				var itemList = this.props.data_me.itemList;\n				var lis = itemList.map(function (item) {\n					var itemClassName = item.className;\n					var contClassName = item.contClassName;\n					var divImgInfo = item.divImgInfo;\n					var divTextInfo = item.divTextInfo;\n					var requestDataTitleDivTextInfo = item.requestDataTitleDivTextInfo;\n					return  (\n						<div className={itemClassName}>\n							<DivImg data_me={divImgInfo}/>\n							<div className={contClassName}>\n								<DivText data_me={divTextInfo}/>								\n								<RequestDataTitleDivText data_me={requestDataTitleDivTextInfo}/>						\n							</div>\n						</div>\n					);\n				}.bind(this));\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<div className={consClassName}>\n							<div className={barClassName}>\n								{lis}\n							</div>\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-03-08 09:30:28', null, '2018-03-08 09:30:28', null);
INSERT INTO `component` VALUES ('97', '2', '图表组件', 'Echarts', '//图表组件\n		class Echarts extends React.Component{\n			constructor(){\n				super();\n				this.state = {\n				  success:false,\n				  resultData:null\n				};\n			}\n			componentDidMount(){\n				var methodType = this.props.data_me.methodType;\n				var url = this.props.data_me.url;\n				var param = this.props.data_me.param;\n				var isPoll = this.props.data_me.isPoll;\n				var pollMsec = this.props.data_me.pollMsec;\n				var optionCallback = {\n					\"onSuccess\":function(json, textStatus, xhr){\n						this.setState({\n						  success:true,\n						  resultData:json\n						});\n					}.bind(this),\n					\"onFailure\": function(xhr, textStatus, error){\n						this.setState({\n						    success:false\n					      });\n					}.bind(this)\n				};\n				this.serverRequest = $.ajax({\n					type: methodType,\n					url: url,\n					data: param,\n					dataType: \"json\",\n					success: function(json, textStatus, xhr){\n						optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n					},\n					error: function(xhr, textStatus, error){\n						optionCallback.onFailure.call(undefined, xhr, textStatus, error);\n					}\n				});\n				if(isPoll) {\n					setInterval(function () {\n					   this.serverRequest = $.ajax({\n							type: methodType,\n							url: url,\n							data: param,\n							dataType: \"json\",\n							success: function(json, textStatus, xhr){\n								optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n							},\n							error: function(xhr, textStatus, error){\n								optionCallback.onFailure.call(undefined, xhr, textStatus, error);\n							}\n						});\n					}.bind(this), pollMsec);\n				}\n			\n			}\n			componentWillUnmount() {\n				this.serverRequest.abort();\n			}\n			componentDidUpdate() {\n				if(this.state.success){\n					var resultData = this.state.resultData;\n					var callbackFun = this.props.data_me.callbackFun;\n					var chart = echarts.init(this.refs.chartInfo);\n					var chartOption = this.props.data_me.chartOption;\n					chart.setOption(chartOption);\n					var isShowLoad = this.props.data_me.isShowLoad;\n					if(isShowLoad){\n						chart.showLoading();\n					}\n					callbackFun(resultData, chart, chartOption);\n				} else {\n					\n				}\n		    }\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				return (\n					<div data-json-ref={dataJsonRef} className={className} ref=\"chartInfo\"></div>\n				);\n		  }\n		}', '1', null, '2018-03-08 09:43:59', null, '2018-03-08 09:43:59', null);
INSERT INTO `component` VALUES ('98', '2', '请求标题图表组件', 'DataTitleCharts', '//请求标题图表组件\n		class DataTitleCharts extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				var investorInfo = this.props.data_me.investorInfo;\n				var echartsInfo = this.props.data_me.echartsInfo;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<RequestDataTitleDivText data_me={investorInfo}/>\n						<Echarts data_me={echartsInfo}/>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-03-08 09:44:35', null, '2018-03-08 09:44:35', null);
INSERT INTO `component` VALUES ('99', '2', '选项卡面板组件', 'TabPanelDiv', '//选项卡面板组件\n		class TabPanelDiv extends React.Component{\n			constructor(){\n				super();\n				this.state = {\n				  current_item:\"\"\n				};\n			}\n			myClick(obj){\n				var barActiveClassName = this.props.data_me.barActiveClassName;\n				var contentHideClassName = this.props.data_me.contentHideClassName;\n				var barRef = obj.ref;\n				var contentRef = obj.ref+\"_content\";\n				$(this.refs[barRef]).siblings().removeClass(barActiveClassName);\n				$(this.refs[barRef]).addClass(barActiveClassName);\n				$(this.refs[contentRef]).siblings().addClass(\"hidden\");\n				$(this.refs[contentRef]).removeClass(\"hidden\");\n				this.setState({\n					current_item:obj,\n				});\n			}\n			componentDidMount(){\n				//第一次渲染 默认是第一个页签\n				this.myClick(this.props.data_me.barList[0]);		\n			}\n			componentWillUnmount() {\n				this.serverRequest.abort();\n			}\n			componentDidUpdate() {\n				var currentItem = this.state.current_item;\n				var currentSource = this.state.current_item.source;\n				var methodType = currentSource.methodType;\n				var url = currentSource.url;\n				var param = currentSource.param;\n				var callbackFun = currentSource.callbackFun;\n				var contentRef = this.refs[currentItem.ref+\"_content\"];\n				var optionCallback = {\n					\"onSuccess\":function(json, textStatus, xhr){\n						callbackFun(json, contentRef);\n					}.bind(this),\n					\"onFailure\": function(xhr, textStatus, error){\n						\n					}.bind(this)\n				};\n				this.serverRequest = $.ajax({\n					type: methodType,\n					url: url,\n					data: param,\n					dataType: \"json\",\n					success: function(json, textStatus, xhr){\n						optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n					},\n					error: function(xhr, textStatus, error){\n						optionCallback.onFailure.call(undefined, xhr, textStatus, error);\n					}\n				});\n		    }\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				var barClassName = this.props.data_me.barClassName;\n				var isMore = this.props.data_me.isMore;\n				var moreInfo = this.props.data_me.moreInfo;\n				var barActiveClassName = this.props.data_me.barActiveClassName;\n				var contentHideClassName = this.props.data_me.contentHideClassName;\n				var barList = this.props.data_me.barList;\n				var barLists = barList.map(function (item) {\n				  return  (\n					<li className={item.className} ref={item.ref} onClick={this.myClick.bind(this,item)}>{item.text}</li>\n				  );\n				}.bind(this));\n				var contentList = this.props.data_me.contentList;\n				var contentLists = contentList.map(function (item) {\n				  return  (\n					<div className={item.className} ref={item.ref}></div>\n				  );\n				}.bind(this));\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<ul className={barClassName} ref=\"tab_bar\">\n							{barLists}\n						</ul>\n						<div className={moreInfo.className} data-url={moreInfo.url} data-plate={moreInfo.plate} onClick={moreInfo.myClick}></div>\n						<div ref=\"tab_content\">\n							{contentLists}\n						</div>\n					</div>				\n				);\n		  }\n		}', '1', null, '2018-03-08 09:46:03', null, '2018-03-08 09:46:03', null);
INSERT INTO `component` VALUES ('100', '2', '公告统计组件', 'NoticeStatisticDiv', '//公告统计组件\n		class NoticeStatisticDiv extends React.Component{\n			constructor(){\n				super();\n			}\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				var data_tab_panel_div = this.props.data_tab_panel_div;\n				var data_statistical_charts = this.props.data_statistical_charts;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<TabPanelDiv data_me={data_tab_panel_div}/>\n						<DataTitleCharts data_me={data_statistical_charts}/>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-03-08 09:46:41', null, '2018-03-08 09:46:41', null);
INSERT INTO `component` VALUES ('101', '2', '面板组件', 'PanelDiv', '//面板组件\n		class PanelDiv extends React.Component{\n			constructor(){\n				super();\n				this.state = {\n				  success:false,\n				  resultData:null\n				};\n			}\n			componentDidMount(){\n				var isRequest = this.props.data_me.isRequest;\n				if(isRequest){\n					var methodType = this.props.data_me.methodType;\n					var url = this.props.data_me.url;\n					var param = this.props.data_me.param;\n					var isPoll = this.props.data_me.isPoll;\n					var pollMsec = this.props.data_me.pollMsec;\n					var optionCallback = {\n						\"onSuccess\":function(json, textStatus, xhr){\n							this.setState({\n							  success:true,\n							  resultData:json\n							});\n						}.bind(this),\n						\"onFailure\": function(xhr, textStatus, error){\n							this.setState({\n								success:false\n							  });\n						}.bind(this)\n					};\n					this.serverRequest = $.ajax({\n							type: methodType,\n							url: url,\n							data: param,\n							dataType: \"json\",\n							success: function(json, textStatus, xhr){\n								optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n							},\n							error: function(xhr, textStatus, error){\n								optionCallback.onFailure.call(undefined, xhr, textStatus, error);\n							}\n						});\n					if(isPoll){\n						setInterval(function () {\n						   this.serverRequest = $.ajax({\n								type: methodType,\n								url: url,\n								data: param,\n								dataType: \"json\",\n								success: function(json, textStatus, xhr){\n									optionCallback.onSuccess.call(undefined, json, textStatus, xhr);\n								},\n								error: function(xhr, textStatus, error){\n									optionCallback.onFailure.call(undefined, xhr, textStatus, error);\n								}\n							});\n						}.bind(this), pollMsec);\n					}\n				} else {\n					this.setState({\n					  success:true\n					});\n				}\n			}\n			componentWillUnmount() {\n				this.serverRequest.abort();\n			}\n			componentDidUpdate() {\n				if(this.state.success){\n					var isRequest = this.props.data_me.isRequest;\n					var callbackFun = this.props.data_me.callbackFun;\n					if(isRequest){\n						var resultData = this.state.resultData;\n						callbackFun(resultData, this.refs.contentRef);\n					} else {\n						var resultData = this.state.resultData;\n						callbackFun(this.refs.contentRef);\n					}\n				}\n		    }\n			render() {\n				var dataJsonRef = this.props.data_me.dataJsonRef;\n				var className = this.props.data_me.className;\n				var barInfo = this.props.data_me.barInfo;\n				var contentClassName = this.props.data_me.contentClassName;\n				return (\n					<div data-json-ref={dataJsonRef} className={className}>\n						<div className={barInfo.className}>\n							<DivImg data_me={barInfo.titleImgInfo}/>\n							<DivText data_me={barInfo.titleInfo}/>\n							<div className={barInfo.moreInfo.className} data-url={barInfo.moreInfo.url} data-plate={barInfo.moreInfo.plate} onClick={barInfo.moreInfo.myClick}></div>\n						</div>\n						<div className={contentClassName} ref=\"contentRef\">\n						</div>\n					</div>\n				);\n		  }\n		}', '1', null, '2018-03-08 09:53:05', null, '2018-03-08 09:53:05', null);
