INSERT INTO `component_instance_tab` VALUES ('86', '100', '5', '\n	//公告统计组件数据源\n	var instance_86_data_me = function(){\n return  {\n						\"dataJsonRef\":\"data_me\",\n						\"className\":\"gg_kfs\",\n					};\n}();\n	//选项卡面板数据源\n	var instance_86_data_tab_panel_div = function(){\n return  {\n					\"dataJsonRef\":\"data_tab_panel_div\",\n					\"className\":\"gg_content\",\n					\"barClassName\":\"gg_tab\",\n					\"isMore\":true,\n					\"moreInfo\":{\n						\"className\":\"tzgg_more_img\",\n						\"url\":\"xxpl/smInfomationList.co\",\n						\"plate\":\"interotc\",\n						\"myClick\":function(event){\n							var _this = $(event.target);\n							var url = _this.attr(\"data-url\");\n							var plate = _this.attr(\"data-plate\");\n							var href = \"\";\n							if (window[\"P_isUserLogin\"]) {\n								href = interfacePath + \"/loginTag/redirectUrlPortal.json?type=\"\n										+ plate + \"&toUri=\" + encodeURIComponent(url);\n							} else {\n								if (plate == \"pe\") {\n									href = interotcUrl + southportal +\"/\"+ url;\n								} else if (plate == \"bond\") {\n									href = interotcUrl + fixedincome +\"/\"+ url;\n								} else if (plate == \"interotc\") {\n									href = interotcUrl +\"/\"+ url;\n								}\n							}\n							//sa.quick(\'trackHeatMap\',this);\n							window.location.href = href;\n						}\n					},\n					\"barActiveClassName\":\"gg_active\",\n					\"contentHideClassName\":\"hidden\",\n					\"barList\":[\n						{\n							\"className\":\"tzgg\",\n							\"text\":\"通知公告\",\n							\"ref\":\"tzgg\",\n							\"source\":{\n								\"methodType\":\"POST\",\n								\"url\":interfacePath + \"/fixedIncome/queryFixedIncomeOnlineInfo.json\",\n								\"param\":{ \"FLID\":\"2\", \"pageIndex\":\"1\", \"pageSize\":\"7\" },\n								\"callbackFun\":function(data,contentRef){\n									if (data.resultSet) {\n										$(contentRef).html(\"\");\n										var htmlStr = \"\";\n										var resultList = data.resultSet;\n										var tzgg_content_ul=$(\"<ul>\",{\"class\":\"tzgg_content_ul\"})\n										if (resultList.length > 0) {\n											for (var i = 0; i < resultList.length; i++) {\n												var tzgg_li=$(\"<li>\");\n												var tzgg_li_a=$(\"<a>\",{\"href\":\"javascript:void(0)\",\"onclick\":\"toTzggDetailFun(\'\"+resultList[i].BDID+\"\')\"});\n												var item_content=$(\"<div>\",{\"class\":\"item_content\",\"html\":resultList[i].TITLE,\"title\":resultList[i].TITLE});\n												var item_date=$(\"<div>\",{\"class\":\"item_date\",\"html\":resultList[i].PUBTIME});\n												tzgg_li_a.append(item_content);\n												tzgg_li.append(tzgg_li_a);\n												tzgg_li.append(item_date);\n												tzgg_content_ul.append(tzgg_li);\n											}\n											$(contentRef).append(tzgg_content_ul);\n										}else{\n											$(contentRef).append(\"&nbsp;&nbsp;&nbsp;&nbsp;暂时无数据显示\");\n										}\n									}\n								}\n							}\n						},\n						{\n							\"className\":\"fxgg\",\n							\"text\":\"发行公告\",\n							\"ref\":\"fxgg\",\n							\"source\":{\n								\"methodType\":\"POST\",\n								\"url\":interfacePath + \"/fixedIncome/lettersList.json\",\n								\"param\":{\"letterBigGroup\":\"1;2;3;4;5\", \"letterStatus\":\"2\", \"pageIndex\": \"1\", \"pageSize\": \"7\"},\n								\"callbackFun\":function(data,contentRef){\n									if (data.resultSet) {\n										$(contentRef).html(\"\");\n										var htmlStr = \"\";\n										var resultList = data.resultSet;\n										var tzgg_content_ul=$(\"<ul>\",{\"class\":\"fxgg_content_ul\"})\n										if (resultList.length > 0) {\n											for (var i = 0; i < resultList.length; i++) {\n												var tzgg_li=$(\"<li>\");\n												var tzgg_li_a=$(\"<a>\",{\"href\":\"javascript:void(0)\",\"onclick\":\"toFxggDetailFun(\'\"+resultList[i].BDID+\"\')\"});\n												var item_content=$(\"<div>\",{\"class\":\"item_content\",\"html\":resultList[i].TITLE,\"title\":resultList[i].TITLE});\n												var item_date=$(\"<div>\",{\"class\":\"item_date\",\"html\":