INSERT INTO `data_json_instance_tab` VALUES ('612', '73', '查询按钮数据源', 'return {\r\n					\"dataJsonRef\":\"data_btn\",\r\n					\"className\":\"search_btn\",\r\n					\"text\":\"搜索\",\r\n					\"myClick\":function(){\r\n						var _cpdmThis = $(\'[data-option-param=\"cpdm\"]\');\r\n						var cpdm = _cpdmThis.val();\r\n						if(cpdm != \"\"){\r\n							if(cpdm.length > 7){\r\n								_cpdmThis.val(\"\");\r\n								_cpdmThis.attr(\"placeholder\",\"请输入六位的产品代码\");\r\n								return;\r\n							}\r\n							var startcpdm = cpdm.substr(0,1);\r\n							if(startcpdm != \"s\" && startcpdm !=\"S\"){\r\n								_cpdmThis.val(\"\");\r\n								_cpdmThis.attr(\"placeholder\",\"请输入以S开头的产品代码\");\r\n								return;\r\n							}\r\n							var endcpdm = cpdm.substr(1,5);\r\n							var strExp=/^[A-Za-z0-9]+$/;\r\n							if(!strExp.test(endcpdm)){//^[A-Za-z0-9]+$\r\n								_cpdmThis.val(\"\");\r\n								_cpdmThis.attr(\"placeholder\",\"请输入合法的产品代码\");\r\n								return;\r\n							}\r\n							console.log(interfacePath + \"/portal/queryCpdm.json\");\r\n							$.ajax({\r\n								type : \"POST\",\r\n								dataType : \"json\",\r\n								data : {\r\n									\"cpdm\" : cpdm\r\n								},\r\n								url : interfacePath + \"/portal/queryCpdm.json\",\r\n								success : function(data, textStatus) {\r\n									if (data.code) {\r\n										var href =\"index.html\";\r\n										var resultList = data.resultSet;\r\n										var message = data.message;\r\n										if (resultList.length > 0) {\r\n											if(window[\"P_isUserLogin\"]){\r\n												if(message == \'pe\'){\r\n													var cplx = resultList[0].CPLX;\r\n													if(cplx==\'5801\' || cplx==\'5802\' || cplx==\'5804\'){\r\n														href = \"html/equityFinancing/detail.html?XBRLID=\"\r\n														+resultList[0].XBLID+\"&ID=\"+resultList[0].ID+\"&CPDM=\"+resultList[0].CPDM+\"&ZT=\"+resultList[0].ZT_BZ;\r\n													} else {\r\n														href = \"html/equityFinancing/detailxm.html?XBRLID=\"\r\n														+resul