INSERT INTO `data_json_instance_tab` VALUES ('676', '86', '图表数据源', 'return  {\n			\"dataJsonRef\":\"data_statistical_charts\",\n			\"className\":\"kfs_content\",\n			\"investorInfo\":{\n					\"dataJsonRef\":\"me_investor_info\",\n					\"isRequest\":true,\n					\"className\":\"kfs_title\",\n					\"titleClassName\":\"\",\n					\"titleText\":\"投资者开户数:\",\n					\"isPoll\":false,\n					\"valClassName\":\"\",\n					\"valKey\":\"KHS\",\n					\"callbackFun\":function(objVal){\n						return objVal;\n					},\n					\"methodType\":\"POST\",\n					\"url\":interfacePath+\"/portal/getStatisticsReport.json\",\n					\"param\":{ \"cxlx\":\"2\", \"count\":\"1\" },\n					\"resultData\":\"resultSet\",\n					\"unitClassName\":\"\",\n					\"unitText\":\"个\"\n				},\n			\"echartsInfo\":{\n					\"dataJsonRef\":\"me_investor_charts\",\n					\"className\":\"img\",\n					\"isPoll\":false,\n					\"methodType\":\"POST\",\n					\"url\":interfacePath + \"/portal/getStatisticsReport.json\",\n					\"param\":{ \"cxlx\" : \"2\" },\n					\"isShowLoad\":true,\n					\"callbackFun\":function(data, chart, option){\n						function convertData(data) {\n							this.data = data;\n							this.init();\n						}\n\n						convertData.prototype = {\n							init : function() {\n								this.mix = parseInt(this.data[0][\'KHS\'] / 10) * 10;\n								this.max = parseInt(this.data[this.data.length - 1][\'KHS\'] / 10) * 10;\n								this.sectionKHS = [];\n								this.sectionZZL = [];\n								this.Xsection = [];\n								var datas= new Array(36);\n								for ( var key in this.data) {\n									if(key <= 35){\n										 datas[key] = this.data[key];\n									}\n								}\n								this.data = datas;\n								this.data.reverse();\n								for ( var key in this.data) {\n										this.sectionKHS.push((this.data[key].KHS / 10000).toFixed(2));\n										this.sectionZZL.push((this.data[key].ZZL / 10000).toFixed(2));\n										var rq = this.data[key].RQ + \"\";\n										this.Xsection.push(rq.substr(2, 2) + \".\" + rq.substr(4, 2));\n								}\n							},\n							getSectionKHS : function() {\n								return this.sectionKHS;\n							},\n							getSectionZZL : function() {\n								return this.sectionZZL;\n							},\n							getSectionX : function() {\n								return this.Xsection;\n							},\n							getMin : function() {\n								return this.min;\n							},\n							getMax : function() {\n								return this.max;\n							},\n\n						}\n						if (data.code) {\n							var obj = new convertData(data.resultSet);\n							option.series[0].data = obj.getSectionKHS();\n							option.series[1].data = 1000-obj.getSectionZZL();\n							option.series[2].data = obj.getSectionZZL();\n							option.xAxis[0].data = obj.getSectionX();\n							chart.setOption(option);\n							chart.hideLoading();\n						}\n					},\n					\"chartOption\":{\n							color : [ \'rgb(221, 131, 68)\', \'rgb(90, 155, 213)\', ],\n							tooltip : {\n								trigger : \'axis\',\n								axisPointer : {\n									type : \'cross\',\n									crossStyle : {\n										color : \'#999\'\n									}\n								},\n								formatter : function(objArr) {\n									var label = objArr[0].name;\n									var time = label.split(\".\");\n									var tag2 = objArr[0].seriesName;\n									var val2 = objArr[0].value;\n									var tag1 = objArr[1].seriesName;\n									var val1 = objArr[1].value;\n									return time[0] + \'年\' + time[1] + \'月\' + \'<hr/>\' + tag1 + \":\"\n											+ val1 + \"(万)<br/>\" + tag2 + \":\" + val2 + \"(万)\";\n\n								},\n							},\n							legend : {\n								data : [ \'累计客户量\', \'新增客户数量\' ],\n								bottom : 0,\n							},\n							xAxis : [ {\n								type : \'category\',\n								data : [],\n								axisPointer : {\n									type : \'shadow\'\n								},\n								axisLabel : {\n									interval : 6,\n									rotate : 36,\n									fontSize : 12,\n								}\n							} ],\n							yAxis : [ {\n								type : \'value\',\n								name : \'累计客户量(万)\',\n								nameTextStyle : {\n									fontSize : 12,\n									padding : [ 0, 0, 0, 35 ],\n								},\n								axisTick : {\n									show : false,\n								},\n								axisLabel : {\n									show : true,\n									margin : 0,\n									fontSize : 12,\n								},\n							}, {\n								type : \'value\',\n								name : \'新增客户数量(万)\',\n								splitLine : {\n									show : false,\n								},\n								nameTextStyle : {\n									padding : [ 0, 35, 0, 0 ],\n								},\n								axisTick : {\n									show : false,\n								},\n								axisLabel : {\n									show : true,\n									margin : 2,\n									fontSize : 12,\n								},\n							} ],\n							dataZoom : [ {\n								type : \'slider\',\n								show : false,\n								start : 0,\n								end : 100,\n								handleSize : 8\n							}, {\n								type : \'inside\',\n								start : 94,\n								end : 100\n							}, ],\n							series : [ {\n								name : \'累计客户量\',\n								type : \'line\',\n								smooth : true,\n								lineStyle : {\n									normal : {\n										width : 4,\n									}\n								},\n								itemStyle : {\n									normal : {\n										borderWidth : 1,\n									}\n								}\n							},{ // For shadow\n								name : \'新增客户数量\',\n								type: \'bar\',\n								itemStyle: {\n									normal: {color: \'rgb(90, 155, 213)\'}\n								},\n								barGap:\'-100%\',\n								barCategoryGap:\'40%\',\n								animation: false,\n							}, {\n								name : \'新增客户数量\',\n								type : \'bar\',\n								yAxisIndex : 1,\n								itemStyle: {\n									normal: {\n										color: new echarts.graphic.LinearGradient(\n											0, 0, 0, 1,\n											[\n												{offset: 0, color: \'#83bff6\'},\n												{offset: 0.5, color: \'#188df0\'},\n												{offset: 1, color: \'#188df0\'}\n											]\n										)\n									},\n									emphasis: {\n										color: new echarts.graphic.LinearGradient(\n											0, 0, 0, 1,\n											[\n												{offset: 0, color: \'#2378f7\'},\n												{offset: 0.7, color: \'#2378f7\'},\n												{offset: 1, color: \'#83bff6\'}\n											]\n										)\n									}\n								},\n							}, ]\n					}\n				}\n			}', null, null, null, null, null, 'data_statistical_charts');
