INSERT INTO `data_json_template_tab` VALUES ('69', '22', 'data_me', '面板数据源', 'return  {\n					\"dataJsonRef\":\"data_me\",\n					\"isRequest\":true,\n					\"className\":\"smgq_area\",\n					\"barInfo\":{\n						\"className\":\"smgq_title_div\",\n						\"titleImgInfo\":{\n							\"dataJsonRef\":\"me_smgq_panel_bar_titleImgInfo\",\n							\"className\":\"smgq_title_img\",\n							\"imgSrc\":\"no\"\n						},\n						\"titleInfo\":{\n							\"dataJsonRef\":\"me_smgq_panel_bar_titleInfo\",\n							\"className\":\"smgq_title\",\n							\"text\":\"私募股权\"\n						},\n						\"moreInfo\":{\n							\"className\":\"smgq_more_img\",\n							\"url\":\"html/equityFinancing/equityFinancingList.html\",\n							\"plate\":\"pe\",\n							\"myClick\":function(event){\n								var _this = $(event.target);\n								var url = _this.attr(\"data-url\");\n								var plate = _this.attr(\"data-plate\");\n								var href = \"\";\n								if (window[\"P_isUserLogin\"]) {\n									href = interfacePath + \"/loginTag/redirectUrlPortal.json?type=\"\n											+ plate + \"&toUri=\" + encodeURIComponent(url);\n								} else {\n									if (plate == \"pe\") {\n										href = interotcUrl + southportal +\"/\"+ url;\n									} else if (plate == \"bond\") {\n										href = interotcUrl + fixedincome +\"/\"+ url;\n									} else if (plate == \"interotc\") {\n										href = interotcUrl +\"/\"+ url;\n									}\n								}\n								//sa.quick(\'trackHeatMap\',this);\n								window.location.href = href;\n							}\n						}\n					},\n					\"contentClassName\":\"smgq_content\",\n					\"isPoll\":false,\n					\"callbackFun\":function(data, contentRef){\n						if (data.resultSet) {\n							$(contentRef).html(\"\");\n							var htmlStr = \"\";\n							var resultList = data.resultSet;\n							if (resultList.length > 0) {\n								for (var i = 0; i < resultList.length; i++) {\n									htmlStr += \' <div class=\"smgq_item\">\'\n										+\'<div class=\"smgq_img\">\'\n										+\'<img src=\"/zzjsInterface/interface/lbdocument/showImgTab.json?tableName=TFP_CPDM&column=LOGO&id=\'\n										+resultList[i].ID\n										+\'\"/>\'\n										+\'<div class=\"smgq_img_zzc\">\'\n										+\'<div class=\"smgq_img_font\">\'\n										+resultList[i].CPMC\n										+\'(\'+resultList[i].CPDM+\')\'\n										+\'</div>\'\n										+\'</div>\'\n										+\'</div>\'\n										+\'<div class=\"rgqd_line\"><span class=\"rgqd_font\">认购起点：</span><span class=\"rgqd_val\">\'\n										+ fmoney(resultList[i].ZDTZMK/10000,2)\n										+\'</span><span class=\"rgqd_dw\">万元</span></div>\'\n										+\'<div class=\"zjjg_line\"><span class=\"zjjg_font\">中介机构：</span><span class=\"zjjg_val\">\'\n										+resultList[i].ZHY_MC\n										+\'</span></div>\'\n										+\'<div class=\"mjqx_line\"><span class=\"mjqx_font\">募集期限：</span><span class=\"mjqx_val\">\'\n										+resultList[i].RGKSRQ\n										+\'-\'\n										+resultList[i].RGJSRQ\n										+\'</span></div>\'\n										+\'<div class=\"jindu_a\"><div class=\"progress_bar\" style=\"width:\'\n										+setProgressBarValFun(resultList[i].RZJD)\n										+\'\"></div></div>\'\n										+\'<div class=\"rzjd_part\">\'\n										+\'<div class=\"rzjd_val\">\'\n										+resultList[i].RZJD\n										+\'%</div>\'\n										+\'<div class=\"rzjd_font\">融资进度</div>\'\n										+\'</div>\'\n										+\'<div class=\"rzje_part\">\'\n										+\'<div class=\"rzje_val\"><span class=\"rzje_sz\">\'\n										+fmoney(resultList[i].ZDJSRZJE/10000,2)\n										+\'</span><span class=\"rzje_dw\">万元</span></div>\'\n										+\'<div class=\"rzje_font\">融资金额</div>\'\n										+\'</div>\'\n										+\'<div class=\"dqrze_part\">\'\n										+\'<div class=\"dqrze_val\"><span class=\"dqrze_sz\">\'\n										+fmoney(resultList[i].AMOUNT/10000,2)\n										+\'</span><span class=\"dqrze_dw\">万元</span></div>\'\n										+\'<div class=\"dqrze_font\">当前融资额</div>\'\n										+\'</div>\'\n										+\'<div class=\"smgq_ckxq_btn_div\">\'\n										+\'<input type=\"button\" value=\"查看详情\" class=\"smgq_ckxq_btn\" data-xblid=\"\'\n										+resultList[i].XBRLID\n										+\'\" data-id=\"\'\n										+resultList[i].ID\n										+\'\" data-cpdm=\"\'\n										+resultList[i].CPDM\n										+\'\" data-zt=\"\'\n										+resultList[i].ZT_BZ\n										+\'\" data-cplx=\"\'\n										+resultList[i].CPLX\n										+\'\" onclick=\"toSmgqDetailFun(this);\"/>\'\n										+\'</div></div>\';\n								}\n								$(contentRef).append(htmlStr);\n							}else{\n								$(contentRef).append(\"&nbsp;&nbsp;&nbsp;&nbsp;暂时无数据显示\");\n							}\n							\n						}\n					},\n					\"methodType\":\"POST\",\n					\"url\":interfacePath + \"/lbProject/equityFinancingList.json\",\n					\"param\":{ \"pageIndex\" : \"1\", \"pageSize\" : \"4\" }\n				}', '1', '2018-03-08 09:57:05', '2', '2018-03-08 09:57:05', null);
