mapper.select:
select 

from ( select ur.ID, ur.USERID, ur.USERNAME, ur.MGID, mc.MGCONTENT, ur.USERMGSTATE, ur.CREATE_TIME, ur.CREATE_USER, ur.UPDATE_TIME, ur.UPDATE_USER from usermessagerela ur join messagecenter mc on mc.id=ur.mgid ) a
where ID = #{id,jdbcType=VARCHAR}

mapper.insert:

  select replace(uuid(),'-','') as uuid

insert into usermessagerela (ID, USERID, USERNAME, 
  MGID, USERMGSTATE, CREATE_TIME, 
  CREATE_USER, UPDATE_TIME, UPDATE_USER
  )
values (#{id,jdbcType=VARCHAR}, #{userid,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, 
  #{mgid,jdbcType=VARCHAR}, #{usermgstate,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}
  )
