	public static void recursive(JSONArray list, int countDesc){
		for( int i = 0 ; i < list.size() ; i++ ) {
			//节点信息对象
			JSONObject nodeInfo = list.getJSONObject(i);
			//标题
			String title = nodeInfo.getString("title");
			//链接
			String link = nodeInfo.getString("link");
			//适用类型
			String applyType = nodeInfo.getString("applytype");
			if(link != null && link.length() > 0){
				HtmlPage page = PageUtils.fetchPage(link);
				String desc = "  ";
				if(page != null){
					List<HtmlParagraph> parentDivs = (List<HtmlParagraph>) page.getByXPath("//p[@class='info']");
					if(parentDivs != null && parentDivs.size() > 0){
						desc = parentDivs.get(0).asText();
						countDesc++;
					}
				}
				nodeInfo.put("desc", desc);

				Map<String, String> map = AliCloudSpider3.queryForInfoByTitle(title);
				if(map != null){
					nodeInfo.put("applytype", map.get("applyType"));
					if("  ".equals(desc)){
						nodeInfo.put("desc", map.get("desc"));
					}
				} else {
					nodeInfo.put("applytype", AliProdusLevel.NOMACTH.getCode());
				}

			} else {
				//下级节点集合信息
				JSONArray subItems = nodeInfo.getJSONArray("subItems");
				//递归
				AliCloudSpider3.recursive(subItems, countDesc);
			}

		}
	}

	/**
	 * 通过产品名称查找对应的信息
	 * @param title
	 * @return 返回null则没有找到
	 */
